'    end loop;',
'',
'    --- compile the response json',
'    apex_json.free_output;',
'    apex_json.initialize_clob_output;',
'',
'    apex_json.open_object;',
'    apex_json.write(''success'', true );',
'    apex_json.close_object;',
'',
'    l_return_json := apex_json.get_clob_output;',
'',
'    apex_json.free_output;',
'',
