'  ',
'    case p_return_type',
'      when ''REGION'' then',
'        return l_region_id;',
'      when ''STATIC'' then ',
'        return l_static_id;',
'    end case;',
'  end get_affected_region;',
'  -------------------------------------------------------',
'  function render_filters',
'    ( p_dynamic_action in apex_plugin.t_dynamic_action ',
'    , p_plugin         in apex_plugin.t_plugin )',
'  return apex_plugin.t_dynamic_action_render_result',
