'    l_filtered_ind varchar2(32767 char ) := p_dynamic_action.attribute_05;',
'    l_config       clob;',
'    l_onload_code  clob;',
'  begin',
'    -- Debug',
'    IF apex_application.g_debug THEN',
'      apex_plugin_util.debug_dynamic_action',
'        ( p_plugin         => p_plugin',
'        , p_dynamic_action => p_dynamic_action);',
